const initialState = {
	quiz: []
}

export default function createRuducer(state = initialState, action) {
	switch (action.type) {
		case CREATE_QUIZ_QUESTION:
		    return {
		    	...state, 
		    	quiz: [...state.quiz, action.item]
		    }
		default:
		return state
	}
}
