import axios from 'axios'

export function auth (email, password, isLogin) {
	return async dispatch => {

		const authData = {
            email, password,
            returnSecureToken: true
        }

        let url = 'https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCbBoRoHs00xHTbFhRuwxba00aAPuNpwQo'

        if (isLogin) {
        	url = 'https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCbBoRoHs00xHTbFhRuwxba00aAPuNpwQo'

        }

        const response = await axios.post(url, authData)
        console.log(response.data)

	}

}
